// page init
jQuery(function(){
	initOpenClose();
	initFilterPost();
	jQuery('input, textarea').placeholder();
});

// init filter posts
function initFilterPost(){
	var limitClass = 'no-more';
	var activeClass = 'active';
	var loadingClass = 'loading';
	var posts = '.post';
	var win = jQuery(window);
	var animSpeed = 1000;
	function getWindowHeight() {
		return typeof window.innerHeight === 'number' ? window.innerHeight : document.documentElement.clientHeight;
	}
	jQuery('.postarea').each(function(){
		var postarea = jQuery(this);
		var nav = postarea.find('.nav-catagory');
		var navItems = nav.find('li');
		var btnAll = navItems.filter('.all');
		var loader = postarea.find('.loader').hide();
		var activeFilter = false;
		// filter posts
		function filterPosts(){
			if(activeFilter){
				navItems.removeClass(activeClass).filter('.'+activeFilter).addClass(activeClass);
				postarea.find(posts).hide().filter('.'+activeFilter).show();
			} else {
				navItems.removeClass(activeClass);
				postarea.find(posts).show();
				btnAll.addClass(activeClass);
			}
		}
		// load posts
		function loadPosts(){
			postarea.addClass(loadingClass);
			loader.fadeIn(animSpeed, function(){
				jQuery.ajax({
					type:'get',
					url: loader.attr('title'),
					dataType: 'html',
					success:function(msg){
						var ajaxItems = jQuery(msg).filter(posts);
						var btnAjax = jQuery(msg).filter('.ajax-more');
						loader.fadeOut(animSpeed, function(){
							postarea.removeClass(loadingClass);
							ajaxItems.insertBefore(loader);
							initOpenClose();
							filterPosts();
						});
						if(btnAjax.length) {
							loader.attr('title', btnAjax.attr('href'));
						} else {
							loader.addClass(limitClass);
						}
					},
					error: function() {
						alert('AJAX Error!');
					}
				});
			});
		}
		// show all posts
		btnAll.children().click(function(e){
			e.preventDefault();
			if(!btnAll.hasClass(activeClass)){
				activeFilter = false;
				filterPosts();
			}
		});
		// handle filter
		navItems.not(btnAll).each(function(){
			var navItem = jQuery(this);
			var filterName = navItem.data('filter');
			var link = navItem.children();
			link.click(function(e){
				e.preventDefault();
				if(!navItem.hasClass(activeClass)){
					activeFilter = filterName;
					filterPosts();
				}
			});
		});
		// scroll event
		win.bind('scroll', function(){
			if(win.scrollTop() + getWindowHeight() >= postarea.offset().top + postarea.height() && !postarea.hasClass(loadingClass) && !loader.hasClass(limitClass)){
				loadPosts();
			}
		});
	});
}

// open-close init
function initOpenClose() {
	jQuery('div.open-close:not(.js-ready)').each(function(){
		var holder = jQuery(this);
		holder.addClass('js-ready').openClose({
			activeClass: 'active',
			opener: '.opener',
			slider: '>.slide',
			animSpeed: 400,
			effect: 'slide',
			animEnd: function(obj){
				if(obj){
					holder.find('.slideshow:not(.js-ready)').each(function(){
						jQuery(this).addClass('js-ready').scrollGallery({
							mask: 'div.mask',
							slider: '.slideset',
							slides: '.slide',
							animSpeed: 500,
							onInit:function(obj){
								obj.slides.find('a').click(function(){
									return false;
								});
							}
						});
					});
				}
			}
		});
	});
}

